mapper.select:
select count(*) 
from product_table
where p_type = #{p_type}

select count(*) 
from product_table
where p_type = #{p_type} and p_brand = #{p_brand}

select * 
from product_table 
where p_type = #{p_type} 
order by p_name
limit #{limit}
offset #{offset}

select * 
from product_table 
where p_type = #{p_type} and p_brand = #{p_brand}
order by p_name
limit #{limit}
offset #{offset}
